package com.propyti.bank.entity;import lombok.Data;import javax.persistence.*;import java.util.List;@Entity@Table(name = "users")@Datapublic class User extends BaseEntity {    @Column(name = "first_name")    private String firstName;    @Column(name = "last_name")    private String lastName;    @Column(name = "uan_score")    private Double uanScore;    @Column(name = "usd_score")    private Double usdScore;    @Column(name = "eur_score")    private Double eurScore;    @Column(name = "email")    private String email;    @Column(name = "password")    private String password;    @ManyToMany(fetch = FetchType.EAGER)    @JoinTable(name = "user_roles",            joinColumns = {@JoinColumn(name = "user_id", referencedColumnName = "id")},            inverseJoinColumns = {@JoinColumn(name = "role_id", referencedColumnName = "id")})    private List<Role> roles;    public User() {    }}